mapper.insert:
INSERT INTO boards(
member_id
, board_id
, category_id
, title
, content
, parent
, reg_date
, hit
, secret
, deleted
)
VALUES(
#{memberId}
, #{boardId}
, #{categoryId}
, #{title}
, #{content}
, #{parent}
, NOW()
, #{hit}
, #{secret}
, false
)

mapper.update:
UPDATE boards
category_id=#{categoryId}, title=#{title}, content=#{content}, secret=#{secret},
WHERE id=#{id}

mapper.select:
SELECT b.id , b.board_id , b.title , b.hit , b.reg_date , c.name AS category_name , m.name AS member_name , (SELECT EXISTS (SELECT id FROM file f WHERE b.id=f.post_id AND f.deleted=false AND f.type='A' LIMIT 1)) AS attached , (SELECT EXISTS (SELECT id FROM file f WHERE b.id=f.post_id AND f.deleted=false AND f.type='C' LIMIT 1)) AS imaged , (SELECT url FROM file f WHERE b.id=f.post_id AND f.deleted=false AND f.type='T' ORDER BY reg_date LIMIT 1) AS thumb_url , (SELECT COUNT(id) FROM comment cm WHERE b.id=cm.post_id) AS comment_count FROM boards b, member m, category c WHERE b.member_id=m.id and b.category_id=c.id AND b.deleted=false
AND b.board_id=#{criteria.boardId}
AND b.category_id=#{criteria.categoryId}
AND (b.title LIKE '%${criteria.searchWord}%' OR b.content LIKE '%${criteria.searchWord}%')
AND (b.reg_date BETWEEN #{criteria.startDate} AND #{criteria.endDate})
AND b.reg_date >= #{criteria.startDate}
AND b.reg_date <= #{criteria.endDate}
ORDER BY b.reg_date DESC LIMIT #{offset},#{limit}

SELECT b.id , b.board_id , b.title , b.hit , b.reg_date , b.mod_date , b.secret , c.name AS category_name , m.name AS member_name , (SELECT EXISTS (SELECT id FROM boards a WHERE a.parent=b.id LIMIT 1)) AS child_exists FROM boards b, member m, category c WHERE b.member_id=m.id and b.category_id=c.id AND b.deleted=false AND b.parent IS NULL
AND b.board_id=#{criteria.boardId}
AND b.category_id=#{criteria.categoryId}
AND b.title LIKE '%${criteria.searchWord}%'
AND (b.reg_date BETWEEN #{criteria.startDate} AND #{criteria.endDate})
AND b.reg_date >= #{criteria.startDate}
AND b.reg_date <= #{criteria.endDate}
ORDER BY b.reg_date DESC LIMIT #{offset},#{limit}

SELECT COUNT(id)
FROM boards
WHERE deleted=false AND parent IS NULL

    AND board_id=#{boardId}


    AND category_id=#{categoryId}


    AND (title LIKE '%${searchWord}%'
    OR content LIKE '%${searchWord}%')



        AND reg_date BETWEEN #{startDate} AND #{endDate}


        AND reg_date >= #{startDate}


        AND reg_date <= #{endDate}
