mapper.insert:
INSERT INTO boards(
member_id
, board_id
, category_id
, title
, content
, reg_date
, hit
)
VALUES(
#{member.id}
, #{boardInfo.id}
, #{category.id}
, #{title}
, #{content}
, NOW()
, 0
)

mapper.select:
SELECT b.id , b.title , b.content , b.reg_date , b.mod_date , b.hit , bi.id AS bi_id , bi.name AS bi_name , bi.subject AS bi_subject , c.id AS c_id , c.name AS c_name , m.id AS m_id , m.username AS m_username , m.name AS m_name , m.role AS m_role , (SELECT EXISTS (SELECT id FROM file f WHERE b.id=f.post_id AND f.del_flag=false AND f.type='A' LIMIT 1)) AS attached , (SELECT EXISTS (SELECT id FROM file f WHERE b.id=f.post_id AND f.del_flag=false AND f.type='C' LIMIT 1)) AS imaged FROM boards b, member m, category c, board_info bi WHERE b.member_id=m.id and b.category_id=c.id AND b.board_id=bi.id AND b.del_flag=false
AND b.board_id=#{criteria.boardId}
AND b.category_id=#{criteria.categoryId}
AND (b.title LIKE '%${criteria.searchWord}%' OR b.content LIKE '%${criteria.searchWord}%')
AND (b.reg_date BETWEEN #{criteria.startDate} AND #{criteria.endDate})
AND b.reg_date >= #{criteria.startDate}
AND b.reg_date <= #{criteria.endDate}
ORDER BY b.reg_date DESC LIMIT #{offset},#{limit}

SELECT COUNT(id)
FROM boards
WHERE del_flag=false

    AND board_id=#{boardId}


    AND category_id=#{categoryId}


    AND (title LIKE '%${searchWord}%'
    OR content LIKE '%${searchWord}%')



        AND reg_date BETWEEN #{startDate} AND #{endDate}


        AND reg_date >= #{startDate}


        AND reg_date <= #{endDate}
