mapper.insert:
INSERT INTO free(
member_id
, category_id
, title
, content
, reg_date
, hit
)
VALUES(
#{memberId}
, #{categoryId}
, #{title}
, #{content}
, NOW()
, 0
)

mapper.select:
SELECT f.id , f.title , f.content , f.reg_date , f.mod_date , f.hit , c.id AS c_id , c.name AS c_name , m.id AS m_id , m.username AS m_username , m.name AS m_name FROM free f, member m, category c WHERE f.member_id=m.id and f.category_id=c.id AND del_flag=false
AND f.category_id=#{criteria.categoryId}
AND (f.title LIKE '%${criteria.searchWord}%' OR f.content LIKE '%${criteria.searchWord}%')
AND (f.reg_date BETWEEN #{criteria.startDate} AND #{criteria.endDate})
AND f.reg_date >= #{criteria.startDate}
AND f.reg_date <= #{criteria.endDate}
ORDER BY f.reg_date DESC LIMIT #{offset},#{limit}

SELECT COUNT(id)
FROM free
WHERE del_flag=false

    AND (title LIKE '%${searchWord}%'
    OR content LIKE '%${searchWord}%')


    AND category_id=#{categoryId}



        AND reg_date BETWEEN #{startDate} AND #{endDate}


        AND reg_date >= #{startDate}


        AND reg_date <= #{endDate}
